mapper.insert:
insert into recipe( recipe_no, recipe_writer, recipe_type, recipe_name, recipe_image, recipe_content, recipe_regdate, recipe_updatedate,
cook_amount, cook_time, cook_level,
material_name, material_amount
)
values( #{recipe_no}, #{recipe_writer}, #{recipe_type}, #{recipe_name}, #{recipe_image}, #{recipe_content}, now(), now(),
#{cook_amount}, #{cook_time}, #{cook_level},
#{material_name}, #{material_amount}
)
